// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.27;

import { TradeTrustTokenBase, ITitleEscrowFactory } from "./base/TradeTrustTokenBase.sol";

/**
 * @title TradeTrustToken
 */
contract TradeTrustToken is TradeTrustTokenBase {
  address internal immutable _titleEscrowFactory;
  uint256 internal immutable _genesis;
  /**
   * @notice Creates a new TradeTrustToken contract.
   * @param name The name of the token.
   * @param symbol The symbol of the token.
   * @param titleEscrowFactory_ The address of the TitleEscrowFactory contract.
   */
  constructor(string memory name, string memory symbol, address titleEscrowFactory_) {
    _genesis = block.number;
    _titleEscrowFactory = titleEscrowFactory_;
    initialize(name, symbol, _msgSender());
  }

  /**
   * @notice Initializes the TradeTrustToken with the provided name, symbol, and admin address.
   * @param name The name of the token.
   * @param symbol The symbol of the token.
   * @param admin The address of the admin.
   */
  function initialize(string memory name, string memory symbol, address admin) internal initializer {
    __TradeTrustTokenBase_init(name, symbol, admin);
  }

  /**
   * @dev See {ITradeTrustSBT-titleEscrowFactory}.
   */
  function titleEscrowFactory() public view override returns (ITitleEscrowFactory) {
    return ITitleEscrowFactory(_titleEscrowFactory);
  }

  /**
   * @dev See {ITradeTrustSBT-genesis}.
   */
  function genesis() public view override returns (uint256) {
    return _genesis;
  }
}

contracts/utils/SigHelper.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.27;

import { ECDSA } from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import { SigHelperErrors } from "../interfaces/SigHelperErrors.sol";

abstract contract SigHelper is SigHelperErrors {
  using ECDSA for bytes32;

  bytes32 public DOMAIN_SEPARATOR;
  mapping(address => uint256) public nonces;
  mapping(bytes32 => bool) public cancelled;

  struct Sig {
    bytes32 r;
    bytes32 s;
    uint8 v;
  }

  function __SigHelper_init(string memory name, string memory version) internal {
    DOMAIN_SEPARATOR = keccak256(
      abi.encode(
        keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"),
        keccak256(bytes(name)),
        keccak256(bytes(version)),
        block.chainid,
        address(this)
      )
    );
  }

  function _validateSig(bytes32 hash, address signer, Sig memory sig) internal view virtual returns (bool) {
    if (cancelled[hash]) {
      revert SignatureAlreadyCancelled();
    }
    bytes32 digest = keccak256(abi.encodePacked("\x19\x01", DOMAIN_SEPARATOR, hash));
    address rSigner = digest.recover(abi.encodePacked(sig.r, sig.s, sig.v));
    return rSigner != address(0) && rSigner == signer;
  }

  function _cancelHash(bytes32 hash) internal virtual {
    if (cancelled[hash]) {
      revert SignatureAlreadyCancelled();
    }
    cancelled[hash] = true;
  }
}

contracts/utils/TDocDeployer.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.27;

import { Clones } from "@openzeppelin/contracts/proxy/Clones.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { TDocDeployerErrors } from "../interfaces/TDocDeployerErrors.sol";

/**
 * @title TDocDeployer
 * @dev Deployer for transferable documents
 */
contract TDocDeployer is OwnableUpgradeable, UUPSUpgradeable, TDocDeployerErrors {
  event Deployment(
    address indexed deployed,
    address indexed implementation,
    address indexed deployer,
    address titleEscrowFactory,
    bytes params
  );

  event AddImplementation(address indexed implementation, address indexed titleEscrowFactory);

  // mapping: implementation => title escrow factory
  mapping(address => address) public implementations;

  constructor() initializer {}

  function initialize() external initializer {
    __Ownable_init(msg.sender);
  }

  function _authorizeUpgrade(address) internal view override onlyOwner {}

  function deploy(address implementation, bytes memory params) external returns (address) {
    address titleEscrowFactory = implementations[implementation];
    if (titleEscrowFactory == address(0)) {
      revert UnsupportedImplementationContractAddress();
    }

    address deployed = Clones.clone(implementation);
    bytes memory payload = abi.encodeWithSignature("initialize(bytes)", abi.encode(params, titleEscrowFactory));
    (bool success, ) = address(deployed).call(payload);
    if (!success) {
      revert ImplementationInitializationFailure(payload);
    }

    emit Deployment(deployed, implementation, msg.sender, titleEscrowFactory, params);
    return deployed;
  }

  function addImplementation(address implementation, address titleEscrowFactory) external onlyOwner {
    if (implementations[implementation] != address(0)) {
      revert ImplementationAlreadyAdded();
    }
    implementations[implementation] = titleEscrowFactory;

    emit AddImplementation(implementation, titleEscrowFactory);
  }

  function removeImplementation(address implementation) external onlyOwner {
    if (implementations[implementation] == address(0)) {
      revert InvalidImplementation();
    }
    delete implementations[implementation];
  }
}
