contracts/base/TradeTrustTokenMintable.s.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import "./TradeTrustSBT.sol";
import "./RegistryAccess.sol";
import "../interfaces/ITradeTrustTokenMintable.sol";

/**
 * @title TradeTrustTokenMintable
 * @dev This contract defines the mint functionality for the TradeTrustToken.
 */
abstract contract TradeTrustTokenMintable is TradeTrustSBT, RegistryAccess, ITradeTrustTokenMintable {
  /**
   * @dev See {ERC165Upgradeable-supportsInterface}.
   */
  function supportsInterface(
    bytes4 interfaceId
  ) public view virtual override(TradeTrustSBT, RegistryAccess) returns (bool) {
    return interfaceId == type(ITradeTrustTokenMintable).interfaceId || super.supportsInterface(interfaceId);
  }

  /**
   * @dev See {ITradeTrustTokenMintable-mint}.
   */
  function mint(
    address beneficiary,
    address holder,
    uint256 tokenId,
    bytes calldata _remark
  ) external virtual override whenNotPaused onlyRole(MINTER_ROLE) returns (address) {
    return _mintTitle(beneficiary, holder, tokenId, _remark);
  }

  /**
   * @dev Internal function to mint a TradeTrust token.
   * @param beneficiary The beneficiary of the token.
   * @param holder The holder of the token.
   * @param tokenId The ID of the token to mint.
   * @return The address of the corresponding TitleEscrow.
   */
  function _mintTitle(
    address beneficiary,
    address holder,
    uint256 tokenId,
    bytes calldata _remark
  ) internal virtual remarkLengthLimit(_remark) returns (address) {
    if (_exists(tokenId)) {
      revert TokenExists();
    }

    address newTitleEscrow = titleEscrowFactory().create(tokenId);
    _safeMint(newTitleEscrow, tokenId, abi.encode(beneficiary, holder, _remark));

    return newTitleEscrow;
  }
}

contracts/base/TradeTrustTokenBase.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import "./RegistryAccess.sol";
import "./TradeTrustTokenBurnable.sol";
import "./TradeTrustTokenMintable.sol";
import "./TradeTrustTokenRestorable.sol";
import "../interfaces/ITradeTrustToken.sol";
import "./TradeTrustTokenBaseURI.sol";

/**
 * @title TradeTrustTokenBase
 * @dev Base token contract for TradeTrust tokens.
 */
abstract contract TradeTrustTokenBase is
  TradeTrustSBT,
  RegistryAccess,
  TradeTrustTokenBaseURI,
  TradeTrustTokenBurnable,
  TradeTrustTokenMintable,
  TradeTrustTokenRestorable
{
  /**
   * @dev Initializss the contract by setting a `name` and a `symbol` to the token contract.
   * @param name The name of the token contract.
   * @param symbol The symbol of the token contract.
   */
  function __TradeTrustTokenBase_init(
    string memory name,
    string memory symbol,
    address admin
  ) internal onlyInitializing {
    __TradeTrustSBT_init(name, symbol);
    __RegistryAccess_init(admin);
  }

  /**
   * @dev See {ERC165Upgradeable-supportsInterface}.
   */
  function supportsInterface(
    bytes4 interfaceId
  )
    public
    view
    virtual
    override(
      TradeTrustSBT,
      RegistryAccess,
      TradeTrustTokenBaseURI,
      TradeTrustTokenRestorable,
      TradeTrustTokenMintable,
      TradeTrustTokenBurnable
    )
    returns (bool)
  {
    return interfaceId == type(ITradeTrustToken).interfaceId || super.supportsInterface(interfaceId);
  }

  /**
   * @dev Pauses all token transfers.
   * @notice Requires the caller to be admin.
   */
  function pause(bytes calldata _remark) external onlyRole(DEFAULT_ADMIN_ROLE) remarkLengthLimit(_remark) {
    _pause();
    remark = _remark;
    emit PauseWithRemark(msg.sender, _remark);
  }

  /**
   * @dev Unpauses all token transfers.
   * @notice Requires the caller to be admin.
   */
  function unpause(bytes calldata _remark) external onlyRole(DEFAULT_ADMIN_ROLE) remarkLengthLimit(_remark) {
    _unpause();
    remark = _remark;
    emit UnpauseWithRemark(msg.sender, _remark);
  }

  /**
   * @dev See {SBTUpgradeable-_beforeTokenTransfer}.
   */
  function _beforeTokenTransfer(
    address from,
    address to,
    uint256 tokenId
  ) internal virtual override(TradeTrustSBT, TradeTrustTokenBurnable) whenNotPaused {
    super._beforeTokenTransfer(from, to, tokenId);

    address titleEscrow = titleEscrowFactory().getAddress(address(this), tokenId);
    if (to != address(this) && to != titleEscrow && to != BURN_ADDRESS) {
      revert TransferFailure();
    }
  }

  /**
   * @dev See {TradeTrustTokenBaseURI-_baseURI}.
   */
  function _baseURI() internal view virtual override(SBTUpgradeable, TradeTrustTokenBaseURI) returns (string memory) {
    return super._baseURI();
  }
}

contracts/base/TradeTrustTokenBurnable.s.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.0;

import "./TradeTrustSBT.sol";
import "./RegistryAccess.sol";
import "../interfaces/ITradeTrustTokenBurnable.sol";

/**
 * @title TradeTrustTokenBurnable
 * @dev This contract defines the burn functionality for the TradeTrustToken.
 */
abstract contract TradeTrustTokenBurnable is TradeTrustSBT, RegistryAccess, ITradeTrustTokenBurnable {
  /**
   * @dev Internal constant for the burn address.
   */
  address internal constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;

  /**
   * @dev See {ERC165Upgradeable-supportsInterface}.
   */
  function supportsInterface(
    bytes4 interfaceId
  ) public view virtual override(TradeTrustSBT, RegistryAccess) returns (bool) {
    return interfaceId == type(ITradeTrustTokenBurnable).interfaceId || super.supportsInterface(interfaceId);
  }

  /**
   * @dev See {ITradeTrustTokenBurnable-burn}.
   */
  function burn(
    uint256 tokenId,
    bytes calldata _remark
  ) external virtual override whenNotPaused onlyRole(ACCEPTER_ROLE) remarkLengthLimit(_remark) {
    _burnTitle(tokenId, _remark);
  }

  /**
   * @dev Internal function to burn a token.
   * @param tokenId The ID of the token to burn.
   */
  function _burnTitle(uint256 tokenId, bytes calldata _remark) internal virtual {
    address titleEscrow = titleEscrowFactory().getAddress(address(this), tokenId);
    ITitleEscrow(titleEscrow).shred(_remark);

    // Burning token to 0xdead instead to show a differentiate state as address(0) is used for unminted tokens
    _registryTransferTo(BURN_ADDRESS, tokenId, "");
  }

  /**
   * @dev See {SBTUpgradeable-_beforeTokenTransfer}.
   */
  function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override {
    if (to == BURN_ADDRESS && ownerOf(tokenId) != address(this)) {
      revert TokenNotSurrendered();
    }
    super._beforeTokenTransfer(from, to, tokenId);
  }
}

